export function withStore(WrappedComponent: any, initialValue?: Object | undefined, config?: {
    listener: ConfigListener;
    logging: boolean;
} | undefined): (props: any) => any;
export type ConfigListener = (state: Object, key: string, prevValue: any, nextValue: any) => void;
/**
 * This store can be used outside of React components.
 */
export let rawStore: {
    reset: (state: Object) => void;
    set: (key: string, value: Object) => void;
    delete: (key: string) => void;
    getState: () => any;
};
